
public class DivisibleExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int counter = 0;
        while (counter < 100 )
        {
            if (counter % 3 == 0 && counter % 5 == 0 && counter!=0)
                System.out.print(counter + ", ");
            counter++;
}

	}

}
